CREATE VIEW parallel_port_view1 AS
             SELECT spcdc.id as record_number, bld.name as building_name, flr.name as floor_name, flt.name as flat_name, rom.name as room_name,rom.host_address as host_address,dvc.name as lpt_comm_device_name, eqpmt.name as equipment_name1, eqpmt1.name as equipment_name2, eqpmt2.name as equipment_name3, eqpmt3.name as equipment_name4, eqpmt4.name as equipment_name5, eqpmt5.name as equipment_name6, eqpmt6.name as equipment_name7, eqpmt7.name as equipment_name8
	  
             FROM smsserver_parallel_comm_device_configuration spcdc INNER JOIN building bld ON bld.id = spcdc.building_id INNER JOIN floor flr ON flr.id = spcdc.floor_id INNER JOIN flat flt ON flt.id = spcdc.flat_id INNER JOIN room rom ON rom.id = spcdc.room_id INNER JOIN device dvc ON dvc.id = spcdc.lpt_comm_device_id INNER JOIN equipment eqpmt  ON eqpmt.id = spcdc.equipment1_id  INNER JOIN equipment eqpmt1  ON eqpmt1.id = spcdc.equipment2_id INNER JOIN equipment eqpmt2  ON eqpmt2.id = spcdc.equipment3_id INNER JOIN equipment eqpmt3  ON eqpmt3.id = spcdc.equipment4_id INNER JOIN equipment eqpmt4  ON eqpmt4.id = spcdc.equipment5_id INNER JOIN equipment eqpmt5  ON eqpmt5.id = spcdc.equipment6_id INNER JOIN equipment eqpmt6  ON eqpmt6.id = spcdc.equipment7_id INNER JOIN equipment eqpmt7  ON eqpmt7.id = spcdc.equipment8_id
 