SELECT spcdc.id as record_number, bld.name as building_name, flr.name as floor_name, flt.name as flat_name, rom.name as room_name,rom.host_address as host_address,dvc.name as lpt_comm_device_name, eqpmt.name as equipment_name1, eqpmt1.name as equipment_name2, eqpmt2.name as equipment_name3, eqpmt3.name as equipment_name4, eqpmt4.name as equipment_name5, eqpmt5.name as equipment_name6, eqpmt6.name as equipment_name7, eqpmt7.name as equipment_name8
	  
             FROM smsserver_parallel_comm_device_configuration spcdc INNER JOIN building bld ON bld.id = spcdc.building_id INNER JOIN floor flr ON spcdc.floor_id = flr.id INNER JOIN flat flt ON spcdc.flat_id = flt.id INNER JOIN room rom ON spcdc.room_id = rom.id INNER JOIN device dvc ON spcdc.lpt_comm_device_id = dvc.id  INNER JOIN equipment eqpmt  ON spcdc.equipment1_id = eqpmt.id INNER JOIN equipment eqpmt1  ON spcdc.equipment2_id = eqpmt1.id  INNER JOIN equipment eqpmt2  ON spcdc.equipment3_id = eqpmt2.id  INNER JOIN equipment eqpmt3  ON spcdc.equipment4_id = eqpmt3.id INNER JOIN equipment eqpmt4  ON spcdc.equipment5_id = eqpmt4.id  INNER JOIN equipment eqpmt5  ON spcdc.equipment6_id = eqpmt5.id  INNER JOIN equipment eqpmt6  ON spcdc.equipment7_id = eqpmt6.id INNER JOIN equipment eqpmt7  ON spcdc.equipment8_id = eqpmt7.id 
 